package com.kk.sj1.p2;

public class DataTypeConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Write a program to assign a value of 100.235 to a double variable and then convert it to int
		double value1 = 100.235;
		int value2 = (int) value1;
		System.out.println("the double value is :"+ value1 );
		System.out.println("the converted double to int value is :"+ value2);
	}

}
